const axios = require("axios");

async function submitRideRequest(source, destination, user_id) {
  try {
    const response = await axios.post("http://localhost:3000/ride-request", {
      source,
      destination,
      user_id,
    });
    console.log("Server Response:", response.data);
  } catch (err) {
    console.error("Error:", err.message);
  }
}

// Example usage
document.getElementById("rideRequestForm").addEventListener("submit", async (event) => {
  event.preventDefault();
  const source = document.getElementById("source").value;
  const destination = document.getElementById("destination").value;
  const user_id = document.getElementById("user_id").value;

  await submitRideRequest(source, destination, user_id);
});