const form = document.getElementById("rideRequestForm");
const responseDiv = document.getElementById("response");

form.addEventListener("submit", async (event) => {
  event.preventDefault();

  const source = document.getElementById("source").value;
  const destination = document.getElementById("destination").value;
  const userId = document.getElementById("user_id").value;

  try {
    const response = await axios.post("http://localhost:3000/ride-request", {
      source,
      destination,
      user_id: userId,
    });
    responseDiv.innerHTML = `Server Response: ${JSON.stringify(response.data)}`;
  } catch (err) {
    responseDiv.innerHTML = `Error: ${err.message}`;
  }
});